import * as ErrorTypeEnum from './consts/ErrorTypeEnum';
import { MessageGroup, MessageTypeEnum } from '../messages';

export default class ErrorObj {
  constructor(type = ErrorTypeEnum.APP, messages, data = {}) {
    this.type = type;
    if (messages instanceof MessageGroup) {
      this.messageGroup = messages;
    } else {
      this.messageGroup = new MessageGroup(MessageTypeEnum.ERROR, messages);
    }

    this.data = data;
  }

  static isException(error) {
    return error instanceof Error;
  }

  static isErrorObj(error) {
    return error instanceof ErrorObj;
  }
}
