import React, { PureComponent, PropTypes } from 'react';

import './index.less';

// eslint-disable-next-line
export default class LoadingBar extends PureComponent { 

  static propTypes = {
    text: PropTypes.string
  }

  render() {
    const { text = '' } = this.props;

    return (
      <div className={'loading-bar'}>
        <div className="loading-bar-mask" />
        <div className="loading-bar-outer-box">
          <div className="loading-bar-icon">
            <i className="icon icon-loading-m icon-spin" />
            <div className="loading-bar-text">{text}</div>
          </div>
        </div>
      </div>
    );
  }
}
