import uniqueId from 'lodash/uniqueId';
import isArray from 'lodash/isArray';
import * as MessageTypeEnum from './consts/MessageTypeEnum';

export default class MessageGroup {
  constructor(type = MessageTypeEnum.ERROR, messages, title = '', id) {
    this.type = type;
    this.title = title;
    this.id = id || uniqueId('message_group_');

    const msgs = [];
    if (!isArray(messages)) {
      msgs.push(messages);
    }

    this.messages = msgs;
  }
}
